import React, { createContext, useState, useEffect } from "react";
import axios from "axios";
import Cookies from 'js-cookie'; // Using js-cookie for easy cookie handling

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const login = () => setIsLoggedIn(true);
  const logout = () => {
    Cookies.remove('authtoken'); // Remove the cookie on logout
    setIsLoggedIn(false);
  };

  useEffect(() => {
    // Check if JWT cookie exists
    const token = Cookies.get('authtoken');
    if (token) {
      axios.get('http://localhost:3001/api/verifyToken', { withCredentials: true }) // Endpoint to verify the token
        .then(response => {
          if (response.status === 200) {
            login(); // Log in if token is valid
          }
        })
        .catch(error => {
          console.error('Token verification failed:', error);
          logout();
        });
    }
  }, []);

  return (
    <AuthContext.Provider value={{ isLoggedIn, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
