import React, { useEffect, useState } from 'react';

const ChecklistPage = () => {
  const [checklistItems, setChecklistItems] = useState([]);

  useEffect(() => {
    // Function to get cookie value by name
    const getCookie = (name) => {
      const value = `; ${document.cookie}`;
      const parts = value.split(`; ${name}=`);
      if (parts.length === 2) return parts.pop().split(';').shift();
    };

    // Fetch checklist items from cookies
    const fetchChecklistFromCookies = () => {
      const checklistCookie = getCookie('checklist');
      if (checklistCookie) {
        setChecklistItems(JSON.parse(checklistCookie));
      }
    };

    fetchChecklistFromCookies();
  }, []);

  const handleAddItem = (e) => {
    e.preventDefault();
    const item = e.target.elements.item.value;

    fetch('http://localhost:3001/api/add-checklist-item', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ item }),
      credentials: 'include',
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message) {
          // Update local state with new item
          setChecklistItems((prevItems) => [...prevItems, item]);
        }
      });
  };

  return (
    <div>
      <h1>Checklist</h1>
      <form onSubmit={handleAddItem}>
        <input type="text" name="item" required />
        <button type="submit">Add Item</button>
      </form>
      <ul>
        {checklistItems.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </div>
  );
};

export default ChecklistPage;
