import React, { useEffect, useState } from 'react';

const ChecklistPage = () => {
  const [checklistItems, setChecklistItems] = useState([]);

  useEffect(() => {
    // Function to get cookie value by name
    const getCookie = (name) => {
      const value = `; ${document.cookie}`;
      const parts = value.split(`; ${name}=`);
      if (parts.length === 2) return parts.pop().split(';').shift();
    };

    // Fetch checklist items from cookies
    const fetchChecklistFromCookies = () => {
      const checklistCookie = getCookie('checklist');
      if (checklistCookie) {
        setChecklistItems(JSON.parse(checklistCookie));
      }
    };

    fetchChecklistFromCookies();
  }, []);

  const handleAddItem = async (newItem) => {
    try {
      const response = await fetch('http://localhost:3001/api/add-checklist-item', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ item: newItem }),
      });
      
      if (response.ok) {
        const data = await response.json();
        if (data.message === 'Item added to checklist') {
          setChecklistItems(prevItems => [...prevItems, newItem]); // Update checklistItems state
        } else {
          console.error('Unexpected response message:', data);
        }
      } else {
        console.error('Failed to add item:', response.status, response.statusText);
      }
    } catch (error) {
      console.error('Error adding checklist item:', error);
    }
  };
  

  return (
    <div>
      <h1>Checklist</h1>
      <form onSubmit={handleAddItem}>
        <input type="text" name="item" required />
        <button type="submit">Add Item</button>
      </form>
      <ul>
        {checklistItems.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </div>
  );
};

export default ChecklistPage;
