import React, { useState } from "react";
import "./SignInPopup.css";
import axios from "axios";

function SignInPopup({ closePopup }) {
  const [isSignIn, setIsSignIn] = useState(true);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const toggleForm = () => {
    setIsSignIn(!isSignIn);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = isSignIn ? "/api/signup" : "/api/login";
    try {
      const response = await axios.post(url, { username, password });
      console.log(response.data);
      closePopup();
      // Implement further logic for handling success, e.g., update the Navbar
    } catch (error) {
      console.error("Authentication failed", error);
    }
  };

  return (
    <div className="popup">
      <div className="popup-inner">
        <button className="close-btn" onClick={closePopup}>
          &times;
        </button>
        <h2>{isSignIn ? "Sign In" : "Log In"}</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">{isSignIn ? "Sign In" : "Log In"}</button>
        </form>
        <button onClick={toggleForm}>
          {isSignIn ? "Switch to Log In" : "Switch to Sign In"}
        </button>
      </div>
    </div>
  );
}

export default SignInPopup;
