import React, { useState, useEffect } from 'react';

const ChecklistPage = () => {
  const [defaultItems, setDefaultItems] = useState([]);
  const [userItems, setUserItems] = useState([]);
  const [newItem, setNewItem] = useState('');

  useEffect(() => {
    // Fetch default checklist items
    fetch('http://localhost:3001/api/checklist/default')
      .then(response => response.json())
      .then(data => setDefaultItems(data))
      .catch(error => console.error('Error fetching default items:', error));

    // Fetch user-specific checklist items
    fetch('http://localhost:3001/api/checklist', { credentials: 'include' })
      .then(response => response.json())
      .then(data => setUserItems(data))
      .catch(error => console.error('Error fetching user items:', error));
  }, []);

  const handleAddItem = () => {
    fetch('http://localhost:3001/api/checklist', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      credentials: 'include',
      body: JSON.stringify({ item: newItem }),
    })
      .then(response => response.json())
      .then(data => {
        setUserItems([...userItems, { item: newItem, is_checked: false }]);
        setNewItem('');
      })
      .catch(error => console.error('Error adding item:', error));
  };

  const handleToggleItem = (id, is_checked) => {
    fetch(`http://localhost:3001/api/checklist/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      credentials: 'include',
      body: JSON.stringify({ is_checked: !is_checked }),
    })
      .then(response => response.json())
      .then(data => {
        setUserItems(userItems.map(item =>
          item.id === id ? { ...item, is_checked: !is_checked } : item
        ));
      })
      .catch(error => console.error('Error updating item:', error));
  };

  return (
    <div>
      <h1>Checklist</h1>
      <div>
        <h2>Default Items</h2>
        <ul>
          {defaultItems.map((item) => (
            <li key={item.id}>{item.item}</li>
          ))}
        </ul>
      </div>
      <div>
        <h2>Your Items</h2>
        <ul>
          {userItems.map((item) => (
            <li key={item.id}>
              <input
                type="checkbox"
                checked={item.is_checked}
                onChange={() => handleToggleItem(item.id, item.is_checked)}
              />
              {item.item}
            </li>
          ))}
        </ul>
        <input
          type="text"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          placeholder="Add a new item"
        />
        <button onClick={handleAddItem}>Add Item</button>
      </div>
    </div>
  );
};

export default ChecklistPage;
